class State extends CI_Controller {
	function __construct()
    { 
        parent:: __construct();
        $this->is_logged_in();
        $this->clear_cache();
    }
	function is_logged_in() 
    {
		
    }
    function clear_cache()
    {
         header('Expires: Sat, 26 Jul 1997 05:00:00 GMT');
        header('Cache-Control: no-cache, no-store, must-revalidate, max-age=0');
        header('Cache-Control: post-check=0, pre-check=0', FALSE);
        header('Pragma: no-cache');
    }
    public function BindBanks()
    {
    	header("Content-Type:application/json");
    	$rslt = $this->db->query("select bank_name as BankName,ifsc from tblbank order by bank_name");
    	echo json_encode($rslt->result());exit;
    }
    public function BindState()
    {
    	header("Content-Type:application/json");
    	$rslt = $this->db->query("select state_name,state_id from tblstate order by state_name");
    	echo json_encode($rslt->result());exit;
    }
    public function BindCity()
    {
        header("Content-Type:application/json");
        $state_id = $this->input->post("state");
        $rslt = $this->db->query("select city_name,city_id from tblcity where state_id = ? order by city_name",array($state_id));
        echo json_encode($rslt->result());exit;
    }
